  const renderSkills = () => (
    <div>
      <h3 style={{ fontSize: '1.25rem', fontWeight: '600', marginBottom: '24px' }}>Capabilities & Skills</h3>

      <p style={{ color: colors.gray, marginBottom: '20px' }}>Select all services your company can provide:</p>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '12px', marginBottom: '24px' }}>
        {skillOptions.map((skill) => (
          <label
            key={skill}
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              padding: '12px 16px',
              backgroundColor: formData.skills.includes(skill) ? `${colors.teal}20` : (darkMode ? colors.dark : '#f8fafc'),
              border: `2px solid ${formData.skills.includes(skill) ? colors.teal : 'transparent'}`,
              borderRadius: '8px',
              cursor: 'pointer',
